INSERT INTO Secciones (Titulo, Articulos, FundamentoLegal, Descripcion, Ayuda, Objetivo, ResponsableNormativo, PublicoObjetivo, Activo) VALUES (N'🔐 Gobernanza y Seguridad', N'Art. 70, 71, 76 Reglamento', N'Establece el control de accesos, perfiles, trazabilidad y clasificación de información.', N'Control de accesos, perfiles, auditoría, firma electrónica y clasificación de la información.', N'Gestiona usuarios, auditorías y seguridad conforme al marco legal.', N'Asegurar la gobernanza, trazabilidad y seguridad del sistema.', N'Dirección General de Tecnologías de la Información y Unidad SNIEr', N'SENER, TICs, Consejo, Sujetos Obligados', 1);
INSERT INTO Secciones (Titulo, Articulos, FundamentoLegal, Descripcion, Ayuda, Objetivo, ResponsableNormativo, PublicoObjetivo, Activo) VALUES (N'📢 Difusión y Fuente Oficial', N'Art. 77 Reglamento', N'Establece que el SNIEr es la única fuente oficial de información energética nacional. Obliga a su citación correcta en todos los documentos oficiales, informes públicos y publicaciones.', N'Mecanismo de difusión oficial y citación obligatoria del SNIEr como fuente de información.', N'Esta sección asegura la transparencia y uso correcto de los datos del SNIEr, estableciendo formatos y lineamientos para su difusión institucional.', N'Garantizar la integridad y autoridad del SNIEr como fuente única de datos energéticos oficiales.', N'Unidad del SNIEr, Dirección de Información Energética, Dirección Jurídica', N'Ciudadanía, OSC, Medios de comunicación, Entidades Públicas, Universidades', 1);
